chrome.runtime.onInstalled.addListener(()=>{console.log("Extension installed"),chrome.storage.local.set({isEnabled:!0,stats:{active:0,pending:0}})});chrome.runtime.onMessage.addListener((t,s,e)=>{if(t.action==="getStats")return chrome.storage.local.get(["stats"],a=>{e(a.stats||{active:0,pending:0})}),!0;t.action==="updateStats"&&(chrome.storage.local.set({stats:t.stats}),e({success:!0}))});chrome.action.onClicked.addListener(t=>{chrome.tabs.create({url:chrome.runtime.getURL("index.html#home")})});
